changeSet.sql:
CREATE TABLE passwords
(
    id       UUID PRIMARY KEY,
    password varchar(255),
    user_id  UUID not null references users(id)
);

CREATE TABLE user_details (
    id UUID PRIMARY KEY,
    avatar varchar,
    about_me varchar,
    user_id UUID not null references users(id)
);

create table roles ( id bigserial primary key, name varchar(50) not null, created_at timestamp default current_timestamp, updated_at timestamp default current_timestamp );
create table users_roles ( user_id UUID not null references users(id), role_id bigint not null references roles (id), created_at timestamp default current_timestamp, updated_at timestamp default current_timestamp, primary key (user_id, role_id) );
insert into roles (name) values ('ROLE_USER'), ('ROLE_ADMIN');
